import React, { useState } from 'react';
import './App.css';

function App() {
  const [entries, setEntries] = useState([]);

  // Funci贸n para agregar un nuevo "row"
  const addRow = () => {
    const newEntry = {
      id: Date.now(),
      date: '',
      activity: '',
      amount: '',
      halfAmount: '',
    };
    setEntries([...entries, newEntry]);
  };

  // Funci贸n para eliminar un "row"
  const removeRow = (id) => {
    setEntries(entries.filter((entry) => entry.id !== id));
  };

  // Calcular el total de amount y halfAmount
  const totalAmount = entries.reduce((acc, entry) => acc + parseFloat(entry.amount || 0), 0);
  const totalHalfAmount = entries.reduce((acc, entry) => acc + parseFloat(entry.halfAmount || 0), 0);

  return (
    <div className="App">
      <div className="App-header">
        <h1>Gastos de viajes</h1>
        <div className="dashboard-main">
          <button className="add-row" onClick={addRow}>+</button>
          <div className="dashboard-entries">
            {entries.map((entry) => (
              <div key={entry.id} className="entry-row">
                <input
                  type="date"
                  value={entry.date}
                  onChange={(e) =>
                    setEntries(entries.map((item) =>
                      item.id === entry.id ? { ...item, date: e.target.value } : item
                    ))
                  }
                />
                <select
                  value={entry.activity}
                  onChange={(e) =>
                    setEntries(entries.map((item) =>
                      item.id === entry.id ? { ...item, activity: e.target.value } : item
                    ))
                  }
                >
                  <option value="">Select Activity</option>
                  <option value="Hotel">Hotel</option>
                  <option value="Carro">Carro</option>
                  <option value="Comida">Comida</option>
                  <option value="Gasolina">Gasolina</option>
                </select>
                <input
                  type="number"
                  placeholder="Amount ($)"
                  value={entry.amount}
                  onChange={(e) =>
                    setEntries(entries.map((item) =>
                      item.id === entry.id ? { ...item, amount: e.target.value, halfAmount: (e.target.value / 2).toFixed(2) } : item
                    ))
                  }
                />
                <input
                  type="text"
                  placeholder="Half Amount"
                  value={`$${entry.halfAmount}`}
                  readOnly
                />
                <button className="remove-row" onClick={() => removeRow(entry.id)}>-</button>
              </div>
            ))}
          </div>
          <div className="dashboard-totals">
            <p>Total Amount: ${totalAmount.toFixed(2)}</p>
            <p>Total Half Amount: ${totalHalfAmount.toFixed(2)}</p>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
