// theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#fff', // Azul por defecto
    },
    secondary: {
      main: '#fff', // Rosa por defecto
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
  },
  components: {
    MuiTextField: {
      styleOverrides: {
        root: {
          marginBottom: '16px',
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: '#1976d2',
            },
            '&:hover fieldset': {
              borderColor: '#f50057',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#f50057',
            },
          },
        },
      },
    },
    MuiInputLabel: {
      styleOverrides: {
        root: {
          color: '#fff', // Color del label por defecto
          '&.Mui-focused': {
            color: '#f50057', // Color del label cuando está enfocado
          },
          '&:hover': {
            color: '#f50057', // Color del label cuando se pasa el mouse por encima
          },
        },
      },
    },
  },
});

export default theme;
