// theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2', // Azul por defecto
    },
    secondary: {
      main: '#f50057', // Rosa por defecto
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
  },
  components: {
    MuiTextField: {
      styleOverrides: {
        root: {
          // Estilo personalizado para todos los TextFields
          marginBottom: '16px', // Espacio debajo de cada TextField
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: '#1976d2', // Color del borde
            },
            '&:hover fieldset': {
              borderColor: '#f50057', // Color del borde al pasar el mouse
            },
            '&.Mui-focused fieldset': {
              borderColor: '#f50057', // Color del borde cuando el campo está enfocado
            },
          },
        },
      },
    },
  },
});

export default theme;
