// theme.js
import { createTheme } from '@mui/material/styles';

const theme = createTheme({
  palette: {
    primary: {
      main: '#1976d2', // Azul por defecto
    },
    secondary: {
      main: '#f50057', // Rosa por defecto
    },
  },
  typography: {
    fontFamily: 'Roboto, Arial, sans-serif',
  },
  components: {
    MuiTextField: {
      styleOverrides: {
        root: {
          marginBottom: '16px',
          '& .MuiOutlinedInput-root': {
            '& fieldset': {
              borderColor: '#1976d2',
            },
            '&:hover fieldset': {
              borderColor: '#f50057',
            },
            '&.Mui-focused fieldset': {
              borderColor: '#f50057',
            },
            '& input': {
              color: '#fff', // Color del texto dentro del campo
            },
          },
        },
      },
    },
    MuiInputLabel: {
      styleOverrides: {
        root: {
          color: '#1976d2',
          '&.Mui-focused': {
            color: '#f50057',
          },
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          backgroundColor: '#1976d2',
          color: '#fff',
          '&:hover': {
            backgroundColor: '#115293',
          },
          '&.Mui-disabled': {
            backgroundColor: '#c0c0c0',
          },
        },
      },
    },
  },
});

export default theme;
